import {
  ChevronRight,
  FavoriteBorder,
  Logout,
  ShoppingBagOutlined,
} from "@mui/icons-material";
import {
  ClickAwayListener,
  Grow,
  MenuList,
  Paper,
  Popper,
  Typography,
} from "@mui/material";
import { MutableRefObject } from "react";
import { Link } from "react-router-dom";
import { DropMenuItem } from "../../../../Styles/Appbar";

interface Props {
  openDropdown: boolean;
  handleClose: (event: Event | React.SyntheticEvent) => void;
  userDropRef: MutableRefObject<HTMLDivElement | null>;
}
function UserDropDown({ openDropdown, handleClose, userDropRef }: Props) {
  return (
    <Popper
      open={openDropdown}
      anchorEl={userDropRef?.current}
      role={undefined}
      placement="bottom-end"
      transition
      disablePortal
      sx={{
        boxShadow: "0 0.5rem 1rem rgb(0 0 0 / 15%)",
        minWidth: "200px",
      }}
    >
      {({ TransitionProps }) => (
        <Grow
          {...TransitionProps}
          style={{
            transformOrigin: "right top",
          }}
        >
          <Paper>
            <ClickAwayListener onClickAway={handleClose}>
              <MenuList
                autoFocusItem={openDropdown}
                id="composition-menu"
                aria-labelledby="composition-button"
                disablePadding
              >
                <DropMenuItem
                  sx={{
                    img: { width: "25px", borderRadius: "50%" },
                  }}
                  onClick={handleClose}
                >
                  <Link to={"/user/status"}>
                    <img
                      src="data:image/png;base64,"
                      alt="avatar"
                    />
                    <Typography>Tohid Ebrahimi</Typography>
                    <ChevronRight sx={{ marginLeft: "auto" }} />
                  </Link>
                </DropMenuItem>
                <DropMenuItem onClick={handleClose}>
                  <Link to="/user/orders">
                    <ShoppingBagOutlined />
                    <Typography>Orders</Typography>
                  </Link>
                </DropMenuItem>
                <DropMenuItem onClick={handleClose}>
                  <Link to="/user/wishlist">
                    <FavoriteBorder />
                    <Typography>Wishlist</Typography>
                  </Link>
                </DropMenuItem>
                <DropMenuItem onClick={handleClose}>
                  <Link to="/">
                    <Logout />
                    <Typography>Logout</Typography>
                  </Link>
                </DropMenuItem>
              </MenuList>
            </ClickAwayListener>
          </Paper>
        </Grow>
      )}
    </Popper>
  );
}

export default UserDropDown;
