import { FormEvent, ChangeEvent, useState, useRef } from "react";
import { CloudUpload } from "@mui/icons-material";
import { CircularProgress, Box, FormControl, Grid, Typography } from "@mui/material";
import { PButton, PFormLabel, POutlinedButton, PStack, PTextField } from "styles/panel";
import { IUser } from "types/user";
import { errorMessage, successMessage } from "utils/toastMessages";
import { useUpdateUserMutation } from "redux/user/userApi";
import { useDispatch } from "react-redux";
import { setCredentials } from "redux/auth/authSlice";
import { uploadImage } from "utils/uploadImage";

interface Props {
  user: IUser;
}

function General({ user }: Props) {
  const [enteredUserName, setEnteredUserName] = useState(user?.userName ?? "");
  const [enteredEmail, setEnteredEmail] = useState(user?.email ?? "");
  const [enteredPhone, setEnteredPhone] = useState(user?.phone ?? "");
  const [uploadedImage, setUploadedImage] = useState(user?.image ?? "");
  const [imgLoading, setImgLoading] = useState(false);
  const [phoneErrorMessage, setPhoneErrorMessage] = useState("");

  const [updateUser] = useUpdateUserMutation();
  const dispatch = useDispatch();
  const ImageRef = useRef<HTMLInputElement>(null);

  const uploadImageHandler = async (event: ChangeEvent<HTMLInputElement>) => {
    const file = event.target?.files![0];
    try {
      setImgLoading(true);
      const data = await uploadImage(file);
      setUploadedImage(data?.url);
      setImgLoading(false);
    } catch (err) {
      errorMessage("image upload failed");
      setImgLoading(false);
      console.log(err);
    }
  };

  const submitHandler = async (event: FormEvent) => {
    event.preventDefault();

    const newUser: any = {
      userName: enteredUserName,
      email: enteredEmail,
      image: uploadedImage ?? "",
    };
    if (enteredPhone && enteredPhone.length >= 10) {
      newUser.phone = enteredPhone;
    }
    if (enteredPhone && enteredPhone.length < 10) {
      setPhoneErrorMessage("your phone must contain at least 10 numbers");
      return;
    }

    try {
      await updateUser({
        user: newUser,
        id: user._id!,
        path: user.role!,
      }).unwrap();
      dispatch(
        setCredentials({
          user: { ...user, ...newUser },
          role: user.role!,
          email: null,
        })
      );
      successMessage("user information updated successfully");
      setPhoneErrorMessage("");
    } catch (err) {
      console.log(err);
      errorMessage("could not update user information");
      setPhoneErrorMessage("");
    }
  };
  return (
    <Grid container spacing={3} component={"form"} onSubmit={submitHandler}>
      <Grid container item xs={12} md={8} spacing={3} sx={{ zIndex: "1 !important" }}>
        <Grid item xs={12}>
          <FormControl fullWidth>
            <PFormLabel>username</PFormLabel>
            <PTextField
              value={enteredUserName}
              onChange={(e) => setEnteredUserName(e.target.value)}
              placeholder="Type Here"
            />
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <FormControl fullWidth>
            <PFormLabel>email</PFormLabel>
            <PTextField
              value={enteredEmail}
              onChange={(e) => setEnteredEmail(e.target.value)}
              placeholder="example@email.com"
            />
          </FormControl>
        </Grid>
        <Grid item xs={12}>
          <FormControl fullWidth>
            <PFormLabel>phone</PFormLabel>
            <PTextField
              value={enteredPhone}
              onChange={(e) => setEnteredPhone(e.target.value)}
              placeholder="+1234567890"
            />
          </FormControl>
          {phoneErrorMessage && (
            <Typography sx={{ color: "#f03637", fontSize: "14px", fontWeight: 500 }}>{phoneErrorMessage}</Typography>
          )}
        </Grid>
      </Grid>
      <Grid item xs={12} md={4}>
        <Box>
          <Box
            sx={{
              position: "relative",
              textAlign: "center",
              overflow: "hidden",
              img: {
                height: "135px",
                aspectRatio: "1",
                objectFit: "cover",
                borderRadius: "50%",
              },
              input: { visibility: "hidden", position: "absolute" },
            }}
          >
            <input type={"file"} ref={ImageRef} onChange={uploadImageHandler} />
            <img
              src={
                uploadedImage ||
                "data:image/png;base64,"
              }
              alt="profile"
            />
          </Box>
          <Box sx={{ textAlign: "center", marginTop: "20px" }}>
            {imgLoading ? (
              <PStack sx={{ margin: "0 !important" }}>
                <CircularProgress color="error" />
              </PStack>
            ) : (
              <POutlinedButton variant="contained" onClick={() => ImageRef!.current!.click()}>
                <CloudUpload sx={{ marginRight: "5px" }} /> upload
              </POutlinedButton>
            )}
          </Box>
        </Box>
      </Grid>
      <Grid item xs={12}>
        <PButton variant="contained" type="submit">
          save changes
        </PButton>
      </Grid>
    </Grid>
  );
}

export default General;
